import React, { useState, useEffect } from 'react';
import { Container, Typography, Button, TextField, List, ListItem, ListItemText, IconButton, Dialog, DialogActions, DialogContent, DialogTitle, AppBar, Toolbar } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

import TodoForm from './TodoForm';
import TodoItem from './TodoItem';
import './App.css';

function App() {
  const [todos, setTodos] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [sorted, setSorted] = useState(false);
  const [openEditDialog, setOpenEditDialog] = useState(false);
  const [editTodoId, setEditTodoId] = useState(null);
  const [editTodoTitle, setEditTodoTitle] = useState('');

  // Fetch initial todos from the API
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/todos')
      .then((response) => response.json())
      .then((data) => {
        setTodos(data);
      });
  }, []);

  // Create a new todo
  const addTodo = (newTodo) => {
    setTodos([...todos, newTodo]);
  };

  // Delete a todo
  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  // Edit an existing todo
  const handleEditTodo = () => {
    setTodos(todos.map(todo => (todo.id === editTodoId ? { ...todo, title: editTodoTitle } : todo)));
    setOpenEditDialog(false);
    setEditTodoId(null);
    setEditTodoTitle('');
  };

  // Sort todos alphabetically
  const toggleSort = () => {
    setSorted(!sorted);
    setTodos([...todos].sort((a, b) => sorted ? a.title.localeCompare(b.title) : b.title.localeCompare(a.title)));
  };

  // Filter todos based on search query
  const filteredTodos = todos.filter(todo =>
    todo.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <Container>
      <AppBar position="sticky">
        <Toolbar>
          <Typography variant="h6" style={{ flexGrow: 1 }}>
            Todo App
          </Typography>
        </Toolbar>
      </AppBar>

      <div style={{ marginTop: '20px' }}>
        {/* Search and Sort Controls */}
        <TextField
          label="Search Todos"
          variant="outlined"
          fullWidth
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{ marginBottom: '20px' }}
        />
        <Button variant="contained" color="success" onClick={toggleSort} style={{ marginBottom: '20px' }}>
          Sort Todos
        </Button>

        {/* Add Todo Form */}
        <TodoForm addTodo={addTodo} />

        {/* Todo List */}
        <List>
          {filteredTodos.map((todo) => (
            <TodoItem
              key={todo.id}
              todo={todo}
              deleteTodo={deleteTodo}
              setOpenEditDialog={setOpenEditDialog}
              setEditTodoId={setEditTodoId}
              setEditTodoTitle={setEditTodoTitle}
            />
          ))}
        </List>
      </div>

      {/* Edit Todo Dialog */}
      <Dialog open={openEditDialog} onClose={() => setOpenEditDialog(false)}>
        <DialogTitle>Edit Todo</DialogTitle>
        <DialogContent>
          <TextField
            label="Todo Title"
            variant="outlined"
            fullWidth
            value={editTodoTitle}
            onChange={(e) => setEditTodoTitle(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenEditDialog(false)} color="error">
            Cancel
          </Button>
          <Button onClick={handleEditTodo} color="success">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default App;
